catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    var targetField = "data_type";
    var targetVariableSet = "data_category_detail";
    var oneTimeChoice = "e308acdc2f13101064e63f96f699b616";

    if (newValue == "" || newValue == oneTimeChoice) {
        g_form.setValue(targetVariableSet, "[]");
    }
    else {
        var currentVal = g_form.getValue(targetVariableSet);
        var jsonArr = [];
        jsonArr = JSON.parse(currentVal);


        g_form.setDisplay(targetVariableSet, true);
        //console.log("jsonArr: " + JSON.stringify(jsonArr));
        var parsedChoices = String(newValue).split(",");
        for (var choice in parsedChoices) {
            var inArr = false;
            for (var i in jsonArr) {
                if (String(jsonArr[i][targetField]) == String(parsedChoices[choice])) {
                    inArr = true; // Already in array so don't overwrite
                }
            }
            if (!inArr) { // Add Choice to the array
                if (String(parsedChoices[choice]) != oneTimeChoice) {
                    var objString = "{\"" + targetField + "\":\"" + String(parsedChoices[choice]) + "\"}";
                    // console.log("objString: " + objString);
                    var jObj = JSON.parse(objString);
                    jsonArr.push(jObj);
                }
            }
        }

        // Remove choices from JSON Array by building new array
        var newJsonArr = [];
        for (var p in jsonArr) {
            var isInArr = false;
            for (var c in parsedChoices) {
                if (typeof (jsonArr[p]) != null && typeof (jsonArr[p][targetField]) != null) {
                    if (String(parsedChoices[c]) == jsonArr[p][targetField]) {
                        isInArr = true;
                        break;
                    }
                }
            }
            if (!isInArr) {
                // Skip - don't add to new array
            }
            else {
                newJsonArr.push(jsonArr[p]);
            }
        }

        //console.log("newJsonArr: " + JSON.stringify(newJsonArr));
        if (newJsonArr.length > 0) {
            g_form.setValue(targetVariableSet, JSON.stringify(newJsonArr));
            var val = g_form.getValue(targetVariableSet);
            if (typeof (val) != "undefined" && val != "" && val != "[]") {
                g_form.setMandatory(targetVariableSet, true);
            }
        }
    }
}
