sys_script_fix.script:
fixScope("sys_script_include", "3373a6922f97501064e63f96f699b6a2", "global"); // GlobalUtils
fixScope("sys_script_include", "dfff81734f120380272e49cf0310c734", "global"); // AutoScaleEventQueues
function fixScope(tableName, recordSid, targetScope) {
	var gr = new GlideRecord(String(tableName));
	if (gr.get(String(recordSid))) {
		gr.sys_scope = String(targetScope);
		gr.update();
	}
}
